import React from "react";
import Sidebar from "../Sidebar";
import Topbar from "../Topbar";
import { useNavigate } from "react-router-dom";
import config from '../../helpers/config.json';

const EditarProducto = () => {
    let navigate = useNavigate();
    let productData = JSON.parse(sessionStorage.getItem("product")); 
    const cancel = () => {
        var {productName, MSU, price, stock, MDPrice, MDPercentage} = document.forms[0]; 
        var hasChanges = productName.value.length > 0 ||  MSU.value.length > 0 || price.value.length > 0 || stock.value.length > 0 ||
            MDPrice.value.length > 0 || MDPercentage.value.length > 0;
        if(hasChanges){
            if(window.confirm("Existen cambios sin guardar. ¿Seguro de querer cancelar?")){
                navigate("/productos");
            }
        } else {
            navigate("/productos")
        }
    }

    const save = async (event) => {
        event.preventDefault();
        var {productName, MSU, price, stock, MDPrice, MDPercentage} = document.forms[0];
        var errors = "";
        errors += parseInt(MDPrice.value) > (parseInt(price) / 2) ? "El descuento por precio no puede superar la mitad del unitario.\n": "";
        errors += parseInt(MDPercentage.value) > 51 ? "El descuento por descuento no puede superar el 50%.\n": "";
        if(errors.length > 0){
            window.alert("Corrija los siguientes errores:\n"+errors);
        } else {
            const requestOptions = {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json'},
                body: JSON.stringify({ "operatorId": config.operatorId, "name": productName.value,"MSU": MSU.value,"price": price.value,"stock": stock.value,
                "MDPrice": MDPrice.value,"MDPercentage": MDPercentage.value, "active": productData.active})
              }
              fetch(config.apiURL+"productos/"+productData.id, requestOptions).then((response) => {
                switch(response.status){
                  case 400:
                    console.log("consulta mal realizada");
                    break;
                  case 403:
                    console.log("acceso prohibido");
                    break;
                  default:
                    //
                }
                return response.json();
              }).then((result) => {
                  window.alert("Actualizacion existosa");
                  navigate("/products");
              })
        }
    }
    return (<div>
            <Topbar />
            <Sidebar />
            <div className="content-wrapper">
                <section className="content-header">
                    <div className="container-fluid">
                        <div className="row mb-2">
                            <div className="col-sm-6">
                                <h1>Editar Producto</h1>
                            </div>
                            <div className="col-sm-6">
                                <ol className="breadcrumb float-sm-right">
                                    <li className="breadcrumb-item"><a href="/">Inicio</a></li>
                                    <li className="breadcrumb-item"><a href="/products">Productos</a></li>
                                    <li className="breadcrumb-item active">Editar</li>
                                </ol>
                            </div>
                        </div>
                    </div>
                </section>
                <section className="content">
                    <div className="card">
                        <div className="card-body">
                            <form onSubmit={save}>
                            <div className="row">
                                <div className="col-12">
                                    <div className="form-group">
                                        <label htmlFor="productName" className="control-label">Nombre</label>
                                        <input type="text" name="productName" id="productName" className="form-control" defaultValue={productData.name} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="MSU" className="control-label">Unidad Minima de Venta</label>
                                        <input type="number" name="MSU" id="MSU" className="form-control" defaultValue={productData.MSU} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="price" className="control-label">Precio</label>
                                        <input type="number" name="price" id="price" className="form-control" defaultValue={productData.price} required />
                                    </div>
                                </div>
                                <div className="col-4">
                                    <div className="form-group">
                                        <label htmlFor="stock" className="control-label">Stock</label>
                                        <input type="number" name="stock" id="stock" className="form-control" defaultValue={productData.stock} required />
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="form-group">
                                        <label htmlFor="MDPrice" className="control-label">Maximo Descuento Precio</label>
                                        <input type="number" name="MDPrice" id="MDPrice" className="form-control" defaultValue={productData.MDPrice} required />
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="form-group">
                                        <label htmlFor="MDPercentage" className="control-label">Maximo Descuento Porcentaje</label>
                                        <input type="number" name="MDPercentage" id="MDPercentage" defaultValue={productData.MDPercentage} className="form-control" max="50" min="0" required />
                                    </div>
                                </div>
                            </div>
                            <div className="d-flex justify-content-between">
                                <button type="button" onClick={cancel} className="btn btn-secondary"><i className="fas fa-times"></i> Cancelar</button>
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Guardar</button>
                            </div>
                            </form>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    )
}

export default EditarProducto;
